import React, { useState } from "react";
import axios from "axios";

export default function CurrentWeather() {
  const [temperature, setTemperature] = useState(null);
  const [wind, setWind] = useState(null);

  let key = "12ct3aa05c4b0c7ae44f804a0oe4060d";
  let units = "metric";
  let url = `https://api.shecodes.io/weather/v1/current?query=London&key=${key}&units=${units}`;

  axios.get(url).then(showWeather);

  function showWeather(response) {
    console.log(response);
    setTemperature(Math.round(response.data.temperature.current));
  }

  return (
    <div>
      <div>
        <h4 className="temperature">{temperature}</h4>
        <span className="unitsCelsia">°C</span>
        <span className="stick">|</span>
        <span className="unitsFarenheit">°F</span>
        <span className="weathername">Clear Sky</span>
        <h1>Kyiv</h1>
      </div>
      <div className="additionalinformation">
        <p>
          <li className="wind">
            Wind <span class="windValue">{}</span>
            <span>m/s</span>
          </li>
          <li className="pressure">
            Pressure <span class="pressureValue">1200</span>
            <span>hPa</span>
          </li>
          <li className="humidity">
            Humidity <span className="humidityValue">1200</span>
            <span>%</span>
          </li>
        </p>
      </div>
    </div>
  );
}
